package com.ml_reader.ml_reader

import android.annotation.SuppressLint
import android.graphics.Bitmap
import android.graphics.BitmapFactory
import android.util.Base64
import androidx.annotation.NonNull
import com.google.mlkit.vision.common.InputImage
import com.google.mlkit.vision.text.TextRecognition
import com.google.mlkit.vision.text.TextRecognizer
import com.google.mlkit.vision.text.latin.TextRecognizerOptions
import io.flutter.embedding.engine.plugins.FlutterPlugin
import io.flutter.plugin.common.MethodCall
import io.flutter.plugin.common.MethodChannel
import io.flutter.plugin.common.MethodChannel.MethodCallHandler
import io.flutter.plugin.common.MethodChannel.Result
import java.io.ByteArrayOutputStream


/** MlReaderPlugin */
class MlReaderPlugin: FlutterPlugin, MethodCallHandler {
  /// The MethodChannel that will the communication between Flutter and native Android
  ///
  /// This local reference serves to register the plugin with the Flutter Engine and unregister it
  /// when the Flutter Engine is detached from the Activity
  private lateinit var channel : MethodChannel
  private lateinit var recognizer: TextRecognizer;

  override fun onAttachedToEngine(@NonNull flutterPluginBinding: FlutterPlugin.FlutterPluginBinding) {
    recognizer = TextRecognition.getClient(TextRecognizerOptions.DEFAULT_OPTIONS)

    channel = MethodChannel(flutterPluginBinding.binaryMessenger, "ml_reader")
    channel.setMethodCallHandler(this)
  }

  @SuppressLint("WrongThread")
  override fun onMethodCall(@NonNull call: MethodCall, @NonNull result: Result) {
    if (call.method == "getPlatformVersion") {
      result.success("Android ${android.os.Build.VERSION.RELEASE}")
    } else if (call.method == "scanImage") {
      val decodedString = "data:image/png;base64,";

      val byteArray = android.util.Base64.decode(decodedString.substringAfter(","), android.util.Base64.DEFAULT)
      val bitMap = android.graphics.BitmapFactory.decodeByteArray(byteArray, 0, byteArray.size)
      val inputImage = InputImage.fromBitmap(bitMap, 0)

      val recognized = recognizer.process(inputImage)
        .addOnSuccessListener { visionText ->
          // Task completed successfully
          // ...
          println("scanned successfully")
          result.success(visionText.text)
        }
        .addOnFailureListener { e ->
          // Task failed with an exception
          // ...
          println("scan failure")
          result.error("unknown", e.message, e)
        }
    } else {
      result.notImplemented()
    }
  }

  override fun onDetachedFromEngine(@NonNull binding: FlutterPlugin.FlutterPluginBinding) {
    recognizer.close()

    channel.setMethodCallHandler(null)
  }
}
